import React, { useState } from "react";
import expand from "../assets/expand_more.svg";
import useUserData from "../hooks/userData";
import UserList from "./UserList";
import FilterModal from "./Modal";

const Transactions = () => {
  const [isFilterModalOpen, setFilterModalOpen] = useState(false);

  const openFilterModal = () => {
    setFilterModalOpen(true);
  };

  const closeFilterModal = () => {
    setFilterModalOpen(false);
  };
  const userData = useUserData();
  const totalTransactions = userData ? userData.length : 0;

  return (
    <div className="flex flex-col pt-10">
      <div className="flex justify-between">
        <div className="text-neutral-900 flex flex-col gap-1">
          <p className="font-[degularbold] text-2xl">{totalTransactions} Users</p>
          <p className="font-[degularmedium] text-gray-600">Number of selected Users</p>
        </div>
        <div className="flex gap-2">
          <button
            className="bg-gray-100 text-neutral-900 p-4 items-center flex gap-2 rounded-full font-[degularsemibold] h-12"
            onClick={openFilterModal}
          >
            Filter
            <img src={expand} alt="expand" />
          </button>
          <input className="bg-gray-100 text-neutral-900 p-4 items-center flex gap-2 rounded-full font-[degularsemibold] h-12" placeholder="Search users">

          </input>
        </div>
      </div>
      <div className="w-full border-t mt-4 border-gray-300" />
      <UserList />
      <FilterModal isOpen={isFilterModalOpen} onClose={closeFilterModal} />
    </div>
  );
};

export default Transactions;
